import { useState, useEffect } from "react";
import {
  addSellByUser,
  deleteProductsInCart,
  getProductToBuy,
  getRowId,
  getTicketId,
} from "../../api/productsApi";
import {
  Image,
  Descriptions,
  Modal,
  Form,
  Comment,
  Row,
  Col,
  Input,
  Button,
  message,
  Card,
} from "antd";
import { useHistory } from "react-router-dom";

import {
  DeleteOutlined,
  CheckCircleOutlined,
  UserOutlined,
} from "@ant-design/icons";
import { sells } from "../Models/sellsModel";
import { getUserAndEmail } from "../../api/userApi";



export default function ProductsToBuy() {
  const _usuarioId = localStorage.getItem("myUser");
  const [products, setProducts] = useState<any | null>();
  const [pagar, setPagar] = useState<number>(0);
  const [visible, setVisible] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const history = useHistory();
  const [rowId, setrowId] = useState<number>(0);
  const [ticket, setTicket] = useState<number>(0);
  const [userName, setUserName] = useState<String>();
  const [correo, setCorreo] = useState<String>();

  let totalPagar = 0;

  useEffect(() => {
    getProductToBuy(_usuarioId as string).then((data: any) => {
      setProducts(data);

      data.forEach((result: any) => {
        totalPagar += result.total;
      });
      setPagar(totalPagar);
    });
  }, [_usuarioId]);

  useEffect(() => {
    getUserAndEmail(_usuarioId as string).then((result) => {
      setUserName(result["nombres"]);
      setCorreo(result["correo"]);
    });
  }, []);

  useEffect(() => {
    getRowId().then((result) => {
      setrowId(result);
    });
  }, []);

  useEffect(() => {
    getTicketId().then((result) => {
      setTicket(result);
    });
  }, []);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = async (values: any) => {
    setIsModalVisible(false);

    values.forEach((result: any) => {
      const _cart = {
        productName: result["productname"],
        price: result["price"],
        quantity: result["quantity"],
        total: result["total"],
        idProduct: result["idproduct"],
        size: result["size"],
        observations: result["observations"],
        usuarioId: _usuarioId,
        rowId: rowId,
        ticketId: ticket,
        buyStatus: "pago recibido",
      } as sells;

      addSellByUser(_cart as sells).then((data) => {});
    });
    setTicket(0);
    setrowId(0);

    
    message.success("El pago fue hecho exitosamente");
    history.push("/");
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const onDeleteProduct = (values: any) => {
    const idProduct = values["idproduct"];
    const usuarioId = values["usuarioid"];

    deleteProductsInCart(idProduct, usuarioId).then((data) => {
      message.success("Producto eliminado del carrito exitosamente");
      setProducts(data);

      data?.forEach((result: any) => {
        totalPagar += result.total;
      });
      setPagar(totalPagar);
    });
  };

  return (
    <>
      <div>{!products && <p>Producto no encontrado</p>}</div>
      <div>
        {products && (
          <div>
            <Row>
              <Col span={16}>
                {products.map((products: any) => (
                  <Row key={products.idproduct}>
                    <Col span={4}>
                      <Card style={{ width: 150, height: 150 }}>
                        <Image
                          width={100}
                          key={products.idproduct}
                          src={products.urlpicture}
                          preview={{
                            visible,
                            src: products.urlPicture,
                            onVisibleChange: (value) => {
                              setVisible(value);
                            },
                          }}
                          onClick={() => setVisible(true)}
                        />
                      </Card>
                    </Col>
                    <Col span={20}>
                      <Card style={{ width: 1050, height: 150 }}>
                        <div>
                          <span style={{ fontWeight: "bold" }}>Producto: </span>
                          <span>{products.productname}</span>
                        </div>
                        <div>
                          <span style={{ fontWeight: "bold" }}>Precio: </span>
                          <span>{products.price}</span>
                        </div>
                        <div>
                          <span style={{ fontWeight: "bold" }}>Cantidad: </span>
                          <span>{products.quantity}</span>
                        </div>
                        <div>
                          <span style={{ fontWeight: "bold" }}>
                            Descripcion:{" "}
                          </span>
                          <span>{products.longdescription}</span>
                        </div>
                        <div>
                          <Button
                            style={{
                              backgroundColor: "lightcoral",
                              fontWeight: "bold",
                            }}
                            shape="round"
                            icon={<DeleteOutlined />}
                            onClick={(event) => {
                              onDeleteProduct(products);
                            }}
                          >
                            Eliminar
                          </Button>
                        </div>
                      </Card>
                    </Col>
                  </Row>
                ))}
              </Col>
            </Row>

            <Row>
              <Col span={16}>
                <Card style={{ width: 1198, height: 100 }}>
                  <div style={{ display: "flex", justifyContent: "end" }}>
                    <span style={{ fontWeight: "bold", paddingRight: "20px" }}>
                      TOTAL:{" "}
                    </span>
                    <span>L.{pagar}.00</span>
                  </div>
                  <div
                    style={{
                      display: "flex",
                      justifyContent: "end",
                      paddingTop: "10px",
                    }}
                  >
                    <Button
                      style={{
                        backgroundColor: "deepSkyBlue",
                        fontWeight: "bold",
                      }}
                      shape="round"
                      onClick={showModal}
                      icon={<CheckCircleOutlined />}
                    >
                      Pagar
                    </Button>
                    <Modal
                      title="MÃ©todo de Pago"
                      visible={isModalVisible}
                      onOk={(event) => handleOk(products)}
                      onCancel={handleCancel}
                      footer={[
                        <Button key="back" onClick={handleCancel}>
                          Cancelar
                        </Button>,
                        <Button
                          key="submit"
                          type="primary"
                          onClick={(event) => handleOk(products)}
                        >
                          Pagar
                        </Button>,
                      ]}
                    >
                      <img
                        className="logo"
                        alt="example"
                        src={
                          "data:image/png;base64,"
                        }
                      />
                      <div className="designdiv1">
                        <h2
                          style={{
                            fontSize: "14px",
                            textAlign: "center",
                            paddingTop: "4px",
                            paddingBottom: "4px",
                          }}
                        >
                          Ingrese su nÃºmero de tarjeta
                        </h2>
                      </div>
                      <div className="designdiv2">
                        <div
                          style={{
                            fontSize: "14px",
                            textAlign: "left",
                            paddingTop: "4px",
                            paddingLeft: "10px",
                          }}
                        >
                          <br />
                          <Input
                            className="input1"
                            prefix={<UserOutlined />}
                            value={"5542 4406 0588 7900"}
                            style={{
                              paddingLeft: "30px",
                              height: "1.5em",
                              textAlign: "left",
                              width: "250px",
                            }}
                          />
                          <span
                            style={{
                              fontWeight: "bold",
                              fontSize: "26px",
                              fontStyle: "italic",
                              paddingLeft: "12px",
                              fontFamily: "Century Schoolbook",
                            }}
                          >
                            VISA
                          </span>
                        </div>
                        <br />
                        <div style={{ display: "flex" }}>
                          <div
                            style={{
                              fontSize: "14px",
                              textAlign: "left",
                              paddingTop: "4px",
                              paddingLeft: "10px",
                            }}
                          >
                            <Input
                              className="input1"
                              value={"08/24"}
                              style={{
                                paddingLeft: "30px",
                                height: "1.5em",
                                textAlign: "left",
                                width: "100px",
                              }}
                            />
                          </div>

                          <div
                            style={{
                              textAlign: "left",
                              paddingTop: "4px",
                              justifyContent: "end",
                              paddingLeft: "135px",
                            }}
                          >
                            <Input
                              className="input1"
                              value={"123"}
                              style={{
                                height: "1.5em",
                                textAlign: "center",
                                width: "100px",
                              }}
                            />
                          </div>
                        </div>
                        <br />
                        <span
                          style={{
                            fontWeight: "bold",
                            fontSize: "18px",
                            fontStyle: "italic",
                            paddingLeft: "12px",
                          }}
                        >
                          NOMBRE DE LA TARJETA
                        </span>
                        <br /> <br />
                      </div>
                    </Modal>
                  </div>
                </Card>
              </Col>
            </Row>
          </div>
        )}
      </div>
    </>
  );
}
